mapper.select:
select * from lib_account

	
		serial=#{serial}
	
	
		email=#{email}
	
	
		name=#{name}

mapper.update:
update lib_account set pass=#{pass}

	, phone=#{phone}


	, address=#{address}


	, interest=#{interest}


	, card=#{card}, carddate=#{carddate}, cardname=#{cardname}, cvc=#{cvc}

where name=#{name}
